import React from 'react';
import { Pie } from 'react-chartjs-2';
import {getData} from './service/Fackserver'



 const getState = () => ({
  labels: ['Total Charter Party Data Upload', 'Total Charter inquiry Actioned','Hr'],
      datasets: [
        {
      data:[2,3,4],
      backgroundColor: ['#31910c', '#ffa500','7c0c0c'],	
          hoverBackgroundColor: [ '#31910c', '#ffa500','7c0c0c']
        }
    ]
});

class Piechart extends React.Component {
state = getState();

componentDidMount = async () => {     
  let response_GetallData = await getData()
  console.log('response_GetallData',response_GetallData);
  let GetAllData = response_GetallData.data;
let  bcapRefNumber = GetAllData.filter(ele=>ele.type == "type1");
 let  cpReferenceNo = GetAllData.filter(ele=>ele.type ==" type2");
let green = bcapRefNumber.length;
let yellow = cpReferenceNo.length;
console.log( green ,yellow);
setInterval(() => {
this.setState(getState(green ,yellow));
}, 1000);
}

render() {  
return (
<div className="flex flex-col items-center w-full ">
  <Pie  data={this.state}  options={{
                          responsive: true,
                          maintainAspectRatio: true,  }}/>
</div>
);
}
}
