import React from 'react'
import { Toast, ToastContainer } from 'react-bootstrap';
import PhoneInput from "react-phone-input-2";
import 'react-phone-input-2/lib/style.css'
import * as yup from 'yup';
import { useFormik } from 'formik';
import { useMutation } from '@apollo/client';
import { SIGNUP_MUTATION } from '../../graphql/mutations/mutation-gql-docs'

const validationSchema = yup.object({
    firstName: yup
        .string()
        .required('Please enter your first name'),
    lastName: yup
        .string()
        .required('Please enter your last name'),
    email: yup
        .string()
        .email('Please enter a valid email')
        .required('Please enter your email'),
    countryCode: yup
        .string(),
    mobileNumber: yup
        .string()
        .required('Please enter your phone number'),
    password: yup
        .string()
        .min(8, 'Password should be of minimum 8 characters length')
        .required('Please enter your password'),
    confirmPassword: yup
        .string()
        .required('Please enter your password again'),
    passwordMatch: yup
        .mixed()
        .test('match', 'Passwords do not match', function () {
            return this.parent.password === this.parent.confirmPassword;
        }),
    tnc: yup.bool().isTrue('Please accept our T&C to proceed')


})

const AddNewUser = () => {
   
   
    const [showToast, setShowToast] = React.useState(false);
    const [message, setMessage] = React.useState('');
    const [register, { loading: regiserLoading }] = useMutation(SIGNUP_MUTATION);



    const handleShowToast = () => setShowToast(true);
    const handleCloseToast = () => setShowToast(false);
    const formik = useFormik({
        initialValues: {
            firstName: '',
            lastName: '',
            email: '',
            countryCode: '+91',
            mobileNumber: '',
            whatsAppNumber: false,
            password: '',
            confirmPassword: '',
            tnc: false
        },
        validationSchema: validationSchema,
        onSubmit: (values, { resetForm }) => {
            register({
                variables: {
                    firstName: values.firstName,
                    lastName: values.lastName,
                    email: values.email,
                    mobileNumber: values.mobileNumber,
                    password: values.password,
                    mobileCountryCode: values.countryCode,
                    whatsAppNumber: values.whatsAppNumber,
                    originSite: 'https://summit.indoglobalb2b.com'
                },
                context: {
                    uri: process.env.REACT_APP_AUTH_URL
                },
            }).then(({ data }) => {
                if (data.createUser) {
                    setMessage('User created successfully');
                    handleShowToast();
                    handleCloseModal();
                }
            })
                .catch(err => {
                    console.log(err.message);
                    setMessage(err.message);
                    handleShowToast();
                });
            resetForm()
        }
    });

 
 
    return(
        <>

<div className="container ">

<div className="d-flex justify-content-center align-items-md-center ">
    <div className="row cover-box  shadow-lg ">
        {/* <div className="align-items-center col-12 col-md-7  col-lg-7 cover-image d-flex d-mob-none">
            <div className="text-white p-5 my-3">
                <h1 className="font-weight-bold">ShiftEasy Central Login Service</h1>
                <h5>Register Login using our central secure server</h5>
                <div>
                    <span>already registered?</span>
                   
                    {keycloak && !keycloak.authenticated
                        ? (<button className="btn btn-primary ms-md-3 ms-lg-3 text-nowrap " type="button" onClick={() => keycloak.login()}>Login</button>)
                        : (
                            <button className="btn btn-primary text-nowrap header-button" type="button" onClick={() => keycloak.logout()}>Logout</button>)
                    }

                </div>
            </div>
        </div> */}
        <div className=" col-12 col-lg-5 col-md-12 bg-white px-xl-5 p-0 login-col">
            <div className="p-3 m-ys-5">

                {/* Register & Login form */}
                <div>
                    <div className="text-end border-bottom-0">
                        <button type="button" className="btn-close login-btn-close text-end" data-bs-dismiss="modal" aria-label="Close">
                            {/* <span className="position-absolute ms-4" aria-hidden="true">&times;</span> */}

                        </button>
                    </div>
                    <ul style={{ display: "flex", justifyContent: "center" }} className="nav nav-pills login-nav-pill mb-2" id="pills-tab" role="tablist">
                        <li className="nav-item" role="presentation">
                            <button className="nav-link active login-nav-links" id="pills-register-tab" data-bs-toggle="pill" data-bs-target="#pills-register" type="button" role="tab" aria-controls="pills-home" aria-selected="true">Register</button>
                        </li>
                        {/* <li className="nav-item" role="presentation">
                                <button className="nav-link login-nav-links" id="pills-login-tab" data-bs-toggle="pill" data-bs-target="#pills-login" type="button" role="tab" aria-controls="pills-profile" aria-selected="false">Login</button>
                            </li>
*/}
                    </ul>
                    <div className="tab-content login-tab-contents" id="pills-tabContent">
                        <div className="tab-pane fade show active login-tab-pan" id="pills-register" role="tabpanel" aria-labelledby="pills-register-tab">
                            <form className="form-horizontal " onSubmit={formik.handleSubmit} noValidate >
                                <div className="form-group mb-2">

                                    <input type="text"
                                        className="form-control  login-fom-control be-0"
                                        placeholder="First Name"
                                        id="firstName"
                                        value={formik.values.firstName}
                                        onChange={formik.handleChange}
                                        onBlur={formik.handleBlur}
                                    />
                                    {formik.touched.firstName &&
                                        (<div className="invalid">
                                            {formik.errors.firstName}
                                        </div>)}

                                </div>
                                <div className="form-group mb-2">
                                    <input type="text"
                                        className="form-control login-fom-control be-0"
                                        id="lastName"
                                        placeholder="Last Name"
                                        value={formik.values.lastName}
                                        onChange={formik.handleChange}
                                        onBlur={formik.handleBlur}
                                    />
                                    {formik.touched.lastName &&
                                        (<div className="invalid">
                                            {formik.errors.lastName}
                                        </div>)}

                                </div>
                                <div className="form-group position-relative mb-2">
                                    <input type="email"
                                        className="form-control login-fom-control be-0"
                                        placeholder="Email Address"
                                        id="email"
                                        value={formik.values.email}
                                        onChange={formik.handleChange}
                                        onBlur={formik.handleBlur}
                                    />
                                    {formik.touched.email &&
                                        (<div className="invalid">
                                            {formik.errors.email}
                                        </div>)}

                                    {/* <img src={info} alt="Info" width="25px" data-toggle="tooltip" data-placement="right" title="Email will sent once confirmed account will be activated" className="info-tooltip" /> */}
                                </div>

                                <div className="form-group position-relative">
                                    <PhoneInput
                                        country={'in'}
                                        value={formik.values.mobileNumber}
                                        onChange={handleMobileNumberChange}
                                        onBlur={() => formik.setTouched({ ['mobileNumber']: true, ...formik.touched })}

                                    />


                                    {formik.touched.mobileNumber &&
                                        (<div className="invalid">
                                            {formik.errors.mobileNumber}
                                        </div>)}
                                </div>
                                <div className="d-flex my-2 flex-row">
                                    <span className="me-2 ">Enable WhatsApp</span>
                                    <div className="form-check form-switch ">

                                        <input type="checkbox"
                                            className="form-check-input"
                                            id="whatsAppNumber"
                                            name="whatsAppNumber"
                                            value={formik.values.whatsAppNumber}
                                            onChange={formik.handleChange}
                                            checked={formik.values.whatsAppNumber}
                                            />

                                        <label className="form-check-label" htmlFor="whatsAppNumber"></label>
                                    </div>
                                    <span className="ms-lg-3 text-black-50 ">(Updates)</span>
                                </div>
                                <div className="form-group mb-2">
                                    <input type="password"
                                        id="password"
                                        className="form-control login-fom-control br-0"
                                        placeholder="Password"
                                        value={formik.values.password}
                                        onChange={formik.handleChange}
                                        onBlur={formik.handleBlur}
                                    />
                                    {formik.touched.password &&
                                        (<div className="invalid">
                                            {formik.errors.password}
                                        </div>)}
                                </div>
                                <div className="form-group mb-2">
                                    <input type="password"
                                        id="confirmPassword"
                                        name="confirmPassword"
                                        className="form-control login-fom-control br-0"
                                        placeholder="Confirm Password"
                                        value={formik.values.confirmPassword}
                                        onChange={formik.handleChange}
                                        onBlur={formik.handleBlur} />
                                    {formik.touched.confirmPassword &&
                                        (<div className="invalid">
                                            {formik.errors.confirmPassword}
                                        </div>)}
                                    {formik.touched.confirmPassword &&
                                        (<div className="invalid">
                                            {!formik.errors.confirmPassword && formik.errors.passwordMatch}
                                        </div>)}
                                </div>

                                <div className="form-check custom-checkbox d-inline-flex">
                                    <input type="checkbox" id="tnc" name="tnc" className="form-check-input me-1" id="terms" value={formik.values.tnc} onChange={formik.handleChange} checked={formik.values.tnc} />
                                    <label className="custom-control-label check-label d-inline" htmlFor="terms">I agree to all the <a href="/terms-conditions" className="login-link-text text-nowrap d-inline">Terms &amp; Conditions</a></label>
                                </div>
                                <button type="submit" className="btn btn-primary shadow my-2 d-block ms-auto" disabled={regiserLoading} onClick={handleShowModal} > Register </button>
                                {/* {formik.touched.tnc &&
                                    (<div className="invalid">
                                        {formik.errors.tnc}
                                    </div>)}
                                <button type="submit" className="btn btn-primary shadow my-2 d-block ms-auto" disabled={regiserLoading} onClick={handleShowModal} >
                                    {regiserLoading && (<span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>)}
                                    Register
                                </button> */}
                            </form>
                        </div>
                       
                        <p className="login-disc">*Central Single SSO Login for ShiftEasy, GotUs.AI, Host.Events, Diagnotica, IndoGlobalB2B, $elZoo, Finasolve Suite of products</p>
                    </div>
                </div>
               
            </div>
        </div>
    </div>
</div>
<ToastContainer position="top-start">
    <Toast show={showToast} onClose={handleCloseToast} autohide>
        <Toast.Body>{message}</Toast.Body>
    </Toast>
</ToastContainer>
</div>

        </>
    )

}

export default AddNewUser
